import "../style/Form.css";
import React, { useState } from "react";
import axios from "axios";
import Alert from "react-bootstrap/Alert";
import { getAuthUser } from "../../helper/Storage";

const AddUser = ({ type }) => {
  const auth = getAuthUser();
  console.log(auth);
  const [user, setUser] = useState({
    name: "",
    password: "",
    email: "",
    phone: "",
    loading: false,
    success: "",
    error: "",
    status: "1",
  });

  // Convert type to lowercase
  const lowerCaseType = type.toLowerCase();

  const Add = (e) => {
    e.preventDefault();
    setUser({ ...user, loading: true, error: "", success: "" });
    const link = `http://localhost:4000/add-${lowerCaseType}`; // Use lowerCaseType here
    console.log(`Sending request to: ${link}`); // Debugging log
    axios
      .post(
        link,
        {
          name: user.name,
          password: user.password,
          email: user.email,
          phone: user.phone,
          status: user.status,
        },
        {
          headers: {
            token: auth.token,
          },
        }
      )
      .then((res) => {
        console.log("Response:", res.data); // Debugging log
        setUser({
          ...user,
          name: "",
          password: "",
          email: "",
          phone: "",
          loading: false,
          error: "",
          success: res.data.message || "User added successfully", // Ensure the success message is handled correctly
        });
      })
      .catch((err) => {
        console.log("Error:", err.response); // Debugging log
        if (err.response && err.response.status === 409) {
          setUser({
            ...user,
            loading: false,
            error: err.response.data.message || "Conflict error",
            success: "",
          });
        } else if (err.response && err.response.status === 422) {
          const errorMsg = err.response.data.errors[0].msg;
          setUser({
            ...user,
            loading: false,
            error: errorMsg,
            success: "",
          });
        } else {
          setUser({
            ...user,
            name: "",
            password: "",
            email: "",
            phone: "",
            error: "Something Went Wrong",
            success: "",
            loading: false,
          });
        }
      });
  };

  // Capitalize the first letter of type for display
  const capitalizedType = type.charAt(0).toUpperCase() + type.slice(1);

  return (
    <>
      <div className="admin-container">
        <div id="shared-table-p">Add {capitalizedType}</div>
        {user.loading === false && user.error && (
          <Alert variant="danger" className="p-2">
            {user.error}
          </Alert>
        )}
        {user.loading === false && user.success && (
          <Alert variant="success" className="p-2">
            {user.success}
          </Alert>
        )}
        <form
          name="myForm"
          className="admin-form"
          onSubmit={Add}
        >
          {/* Form fields */}
        </form>
      </div>
    </>
  );
};

export default AddUser;
